import "./MainView.css";
import CityCard from "../components/CityCard.js";
import { AiOutlinePlus } from 'react-icons/ai';
import { useState,useEffect} from 'react';

function MainView() {

    const [city,setCity]=useState('')
  useEffect(()=>{
    const newCity=document.getElementById("nc")
    console.log(newCity)
  },[])  


    const addCity=()=>{
        console.log(newCity)
    }
    
    return (
        <div class="singleview">
            <div class="header">
                <div class="logoname">
                    <img class="applogo" src="https://cdn-icons-png.flaticon.com/512/1163/1163712.png"></img>
                    <h1 class="appname">Weather <strong>App</strong></h1>
                </div>
            </div>
            <div class="searching">
                <div class="searchbar">
                    <input class="searchinput" type="text" placeholder="Dodaj miasto" id="nc" />
                    <AiOutlinePlus onClick={addCity} size={'1.5rem'} style={{ color: "rgb(72, 72, 72)" }} />
                </div>
                </div>
            <div class="space">
                <CityCard />
            </div>
        </div>
    )
}

export default MainView
