import "./CityCard.css"
import { TiDeleteOutline } from 'react-icons/ti';
import { useEffect, useState } from "react";

function CityCard({ name },{newCities}) {
    const [cityData, setCityData] = useState()
    useEffect(() => {
        const fetchData = async() =>  {
            const res = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${name}&cnt=1&APPID=3b38d9cd69cbc01a769e639cdf78004d`);
            console.log('res', res);
            const data = await res.json();
            console.log('data', data);
            setCityData(data)
            
        };
        fetchData();

    }, []);

    const KelvinToCelcius=(kelvin)=>{
        const celcius=Math.round((kelvin - 273.15)*10)/10
        return celcius
    }

    const DeleteFromBase=(e)=>{
        if(e.target.name !=="")
        {
            newCities.pop()
            console.log(newCities)
            const localStorageValue=JSON.parse(localStorage.getItem("city"))
            delete localStorageValue[name,e.target.name]
            localStorage.setItem("city",JSON.stringify(newCities));
            console.log(localStorageValue)
        }
    }


    return (
        <div class="citycard">
            <div class="weathericon">
                <img style={{ height: '7rem' }} src="https://img.icons8.com/nolan/452/smiling-sun.png"></img>
            </div>
            <div class="text">
                <div class="city">{name}</div>
                <div class="temp">{KelvinToCelcius(cityData?.list?.[0]?.main?.temp)}°C</div>
            </div>
            <div class="buttons">
                <div class="delete" onClick={DeleteFromBase}>
                    <div class="deletetext">Usuń</div>
                    <TiDeleteOutline size={'1.5rem'} style={{ color: "rgb(72, 72, 72)" }} />
                </div>
                <div class="info">
                    <div class="infobutton">O mieście</div>
                </div>
            </div>
        </div>
    )
}

export default CityCard
