import "./MainView.css";
import CityCard from "../components/CityCard.js";
import { AiOutlinePlus } from 'react-icons/ai';
import { useState } from 'react'

function MainView() {

    const [cityId, setCityId] = useState(0)
    const [newCity, setNewCity] = useState([])

    const city = {
        id: cityId,
        name: newCity,
    };

    const clearI = () => {
        const ival = document.getElementById("nc")
        if (ival.value !== "") {
            ival.value = ""
        }
    }
    const clearAll = () => {
        localStorage.clear()
        setCityId(0)
        setNewCity([])
    }
    const addCity = () => {
        const item = document.getElementById("nc").value
        if (item !== "" && item !== null && item !== '/0') {
            if(newCity.length<=4){
            newCity[cityId] = item
            if (newCity.length <= 4) {
                localStorage.setItem('city', JSON.stringify(newCity), [])
                setCityId(cityId + 1)
            }
            else { alert("Przekroczono limit miast!") }
            clearI()
        }
    }
        else { alert("Wpisz nazwę miasta!") }
        return(item)
    }

    const Cards = newCity.map((item) => <CityCard />)

    return (
        <div class="singleview">
            <div class="header">
                <div class="logoname">
                    <img class="applogo" src="https://cdn-icons-png.flaticon.com/512/1163/1163712.png"></img>
                    <h1 class="appname">Weather <strong>App</strong></h1>
                </div>
            </div>
            <div class="searching">
                <div class="searchbar">
                    <input class="searchinput" type="text" placeholder="Dodaj miasto" id="nc" />
                    <AiOutlinePlus size={'1.5rem'} style={{ color: "rgb(72, 72, 72)" }} onClick={addCity} />
                </div>
            </div>
            <div class="space">
                {Cards}
            </div>
        </div>
    )
}

export default MainView
